package Test;

public class ShortcutOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int x = 10;
		System.out.println(x);
		x = 1;
		 
		 System.out.println(x + 1);
		 System.out.println(x);
		 
		 System.out.println("*****************************");
		 
		 int w = 5;
		 int e = 10;
		 
		 w = e;
		 e = w;
		 
		 System.out.println(10 + e);

	}

}
